func PluginEntityStream(q fireback.QueryDSL) (chan []*PluginEntity, *fireback.QueryResultMeta, *fireback.IError) {
	cn := make(chan []*PluginEntity)
	q.ItemsPerPage = 50
	q.StartIndex = 0
	_, qrm, err := PluginActions.Query(q)
	if err != nil {
		return nil, nil, err
	}
	go func() {
		defer close(cn)
		for i := 0; i <= int(qrm.TotalAvailableItems)-1; i++ {
			items, _, _ := PluginActions.Query(q)
			i += q.ItemsPerPage
			q.StartIndex = i
			cn <- items
		}
	}()
	return cn, qrm, nil
}

