func PluginActionBulkUpdate(
	query fireback.QueryDSL, dto *fireback.BulkRecordRequest[PluginEntity]) (
	*fireback.BulkRecordRequest[PluginEntity], *fireback.IError,
) {
	result := []*PluginEntity{}
	err := fireback.GetDbRef().Transaction(func(tx *gorm.DB) error {
		query.Tx = tx
		for _, record := range dto.Records {
			item, err := PluginActions.Update(query, record)
			if err != nil {
				return err
			} else {
				result = append(result, item)
			}
		}
		return nil
	})
	if err == nil {
		return dto, nil
	}
	return nil, err.(*fireback.IError)
}
