func CastPluginFromCli(c *cli.Context) *PluginEntity {
	template := &PluginEntity{}
	fireback.HandleXsrc(c, template)
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		template.ParentId = fireback.NewStringAutoNull(c.String("pid"))
	}
	if c.IsSet("name") {
		template.Name = c.String("name")
	}
	if c.IsSet("content") {
		template.Content = fireback.NewXFileAutoNull(c.String("content"))
	}
	if c.IsSet("enabled") {
		template.Enabled = fireback.NewBoolAutoNull(c.String("enabled"))
	}
	return template
}
