var categoryMemoryItems []*CategoryEntity = []*CategoryEntity{}

func CategoryEntityIntoMemory() {
	q := fireback.QueryDSL{
		ItemsPerPage: 500,
		StartIndex:   0,
	}
	_, qrm, _ := CategoryActions.Query(q)
	for i := 0; i <= int(qrm.TotalAvailableItems)-1; i++ {
		items, _, _ := CategoryActions.Query(q)
		categoryMemoryItems = append(categoryMemoryItems, items...)
		i += q.ItemsPerPage
		q.StartIndex = i
	}
}
func CategoryMemGet(id uint) *CategoryEntity {
	for _, item := range categoryMemoryItems {
		if item.ID == id {
			return item
		}
	}
	return nil
}
func CategoryMemJoin(items []uint) []*CategoryEntity {
	res := []*CategoryEntity{}
	for _, item := range items {
		v := CategoryMemGet(item)
		if v != nil {
			res = append(res, v)
		}
	}
	return res
}
func (dto *CategoryEntity) Size() int {
	var size int = len(dto.Children)
	for _, c := range dto.Children {
		size += c.Size()
	}
	return size
}
func (dto *CategoryEntity) Add(nodes ...*CategoryEntity) bool {
	var size = dto.Size()
	for _, n := range nodes {
		if n.ParentId.Valid && n.ParentId.String == dto.UniqueId {
			dto.Children = append(dto.Children, n)
		} else {
			for _, c := range dto.Children {
				if c.Add(n) {
					break
				}
			}
		}
	}
	return dto.Size() == size+len(nodes)
}
