func CategoryActionCteQueryFn(query fireback.QueryDSL) ([]*CategoryEntity, *fireback.QueryResultMeta, *fireback.IError) {
	refl := reflect.ValueOf(&CategoryEntity{})
	items, meta, err := fireback.ContextAwareVSqlOperation[CategoryEntity](
		refl, &queries.QueriesFs, "CategoryCte.vsql", query,
	)
	for _, item := range items {
		entityCategoryFormatter(item, query)
	}
	var tree []*CategoryEntity
	for _, item := range items {
		if !item.ParentId.Valid {
			root := item
			root.Add(items...)
			tree = append(tree, root)
		}
	}
	return tree, meta, err
}
func CategoryUpdateExec(dbref *gorm.DB, query fireback.QueryDSL, fields *CategoryEntity) (*CategoryEntity, *fireback.IError) {
	uniqueId := fields.UniqueId
	query.TriggerEventName = CATEGORY_EVENT_UPDATED
	CategoryEntityPreSanitize(fields, query)
	var item CategoryEntity
	var itemRefetched CategoryEntity
	// If the entity is distinct by workspace, then the Query.WorkspaceId
	// which is selected is being used as the condition for create or update
	// if not, the unique Id is being used
	cond2 := &CategoryEntity{UniqueId: uniqueId}
	q := dbref.
		Where(cond2).
		FirstOrCreate(&item)
	err := q.UpdateColumns(fields).Error
	if err != nil {
		return nil, fireback.GormErrorToIError(err)
	}
	query.Tx = dbref
	CategoryRelationContentUpdate(fields, query)
	CategoryPolyglotUpdateHandler(fields, query)
	if ero := CategoryDeleteEntireChildren(query, fields); ero != nil {
		return nil, ero
	}
	// @meta(update has many)
	if fields.Parameters != nil {
		linkerId := uniqueId
		dbref.
			Where(&CategoryParameters{LinkerId: fireback.NewString(linkerId)}).
			Delete(&CategoryParameters{})
		for _, newItem := range fields.Parameters {
			newItem.UniqueId = fireback.UUID()
			newItem.LinkerId = fireback.NewString(linkerId)
			dbref.Create(&newItem)
		}
	}
	err = dbref.
		Preload(clause.Associations).
		Where(&CategoryEntity{UniqueId: uniqueId}).
		First(&itemRefetched).Error
	if err != nil {
		return nil, fireback.GormErrorToIError(err)
	}
	actionEvent, eventErr := NewCategoryUpdatedEvent(fields, &query)
	if actionEvent != nil && eventErr == nil {
		fireback.GetEventBusInstance().FireEvent(query, *actionEvent)
	} else {
		log.Default().Panicln("Updating event has failed for %v", fields)
	}
	/*
	   event.MustFire(query.TriggerEventName, event.M{
	     "entity":   &item,
	     "target":   "workspace",
	     "unqiueId": query.WorkspaceId,
	   })*/
	return &itemRefetched, nil
}
func CategoryActionUpdateFn(query fireback.QueryDSL, fields *CategoryEntity) (*CategoryEntity, *fireback.IError) {
	if fields == nil {
		return nil, fireback.Create401Error(&fireback.FirebackMessages.BodyIsMissing, []string{})
	}
	// 1. Validate always
	if iError := CategoryValidator(fields, true); iError != nil {
		return nil, iError
	}
	// Let's not add this. I am not sure of the consequences
	// CategoryRecursiveAddUniqueId(fields, query)
	var dbref *gorm.DB = nil
	if query.Tx == nil {
		dbref = fireback.GetDbRef()
		var item *CategoryEntity
		vf := dbref.Transaction(func(tx *gorm.DB) error {
			dbref = tx
			var err *fireback.IError
			item, err = CategoryUpdateExec(dbref, query, fields)
			if err == nil {
				return nil
			} else {
				return err
			}
		})
		return item, fireback.CastToIError(vf)
	} else {
		dbref = query.Tx
		return CategoryUpdateExec(dbref, query, fields)
	}
}

