func CategoryActionBulkUpdate(
	query fireback.QueryDSL, dto *fireback.BulkRecordRequest[CategoryEntity]) (
	*fireback.BulkRecordRequest[CategoryEntity], *fireback.IError,
) {
	result := []*CategoryEntity{}
	err := fireback.GetDbRef().Transaction(func(tx *gorm.DB) error {
		query.Tx = tx
		for _, record := range dto.Records {
			item, err := CategoryActions.Update(query, record)
			if err != nil {
				return err
			} else {
				result = append(result, item)
			}
		}
		return nil
	})
	if err == nil {
		return dto, nil
	}
	return nil, err.(*fireback.IError)
}
