func ParameterMultiInsertFn(dtos []*ParameterEntity, query fireback.QueryDSL) ([]*ParameterEntity, *fireback.IError) {
	if len(dtos) > 0 {
		for index := range dtos {
			ParameterEntityPreSanitize(dtos[index], query)
			ParameterEntityBeforeCreateAppend(dtos[index], query)
		}
		var dbref *gorm.DB = nil
		if query.Tx == nil {
			dbref = fireback.GetDbRef()
		} else {
			dbref = query.Tx
		}
		query.Tx = dbref
		err := dbref.Create(&dtos).Error
		if err != nil {
			return nil, fireback.GormErrorToIError(err)
		}
	}
	return dtos, nil
}
