func ParameterActionCreateFn(dto *ParameterEntity, query fireback.QueryDSL) (*ParameterEntity, *fireback.IError) {
	// 1. Validate always
	if iError := ParameterValidator(dto, false); iError != nil {
		return nil, iError
	}
	// 1.5 Sanitize the content coming of the front-end
	ParameterEntityPreSanitize(dto, query)
	// 2. Append the necessary information about user, workspace
	ParameterEntityBeforeCreateAppend(dto, query)
	// 3. Create other entities if we want select from them
	ParameterRelationContentCreate(dto, query)
	// 4. Create the entity
	var dbref *gorm.DB = nil
	if query.Tx == nil {
		dbref = fireback.GetDbRef()
	} else {
		dbref = query.Tx
	}
	query.Tx = dbref
	err := dbref.Create(&dto).Error
	if err != nil {
		err := fireback.GormErrorToIError(err)
		return nil, err
	}
	// 5. Create sub entities, objects or arrays, association to other entities
	ParameterAssociationCreate(dto, query)
	// 6. Fire the event into system
	actionEvent, eventErr := NewParameterCreatedEvent(dto, &query)
	if actionEvent != nil && eventErr == nil {
		fireback.GetEventBusInstance().FireEvent(query, *actionEvent)
	} else {
		log.Default().Panicln("Creating event has failed for %v", dto)
	}
	/*
		event.MustFire(PARAMETER_EVENT_CREATED, event.M{
			"entity":   dto,
			"entityKey": fireback.GetTypeString(&ParameterEntity{}),
			"target":   "workspace",
			"unqiueId": query.WorkspaceId,
		})
	*/
	return dto, nil
}
