func ParameterActionWipeClean(query fireback.QueryDSL) (int64, error) {
	var err error
	var count int64 = 0
	{
		subCount, subErr := fireback.WipeCleanEntity[ParameterEntity]()
		if subErr != nil {
			fmt.Println("Error while wiping 'ParameterEntity'", subErr)
			return count, subErr
		} else {
			count += subCount
		}
	}
	return count, err
}
func ParameterActionBulkUpdate(
	query fireback.QueryDSL, dto *fireback.BulkRecordRequest[ParameterEntity]) (
	*fireback.BulkRecordRequest[ParameterEntity], *fireback.IError,
) {
	result := []*ParameterEntity{}
	err := fireback.GetDbRef().Transaction(func(tx *gorm.DB) error {
		query.Tx = tx
		for _, record := range dto.Records {
			item, err := ParameterActions.Update(query, record)
			if err != nil {
				return err
			} else {
				result = append(result, item)
			}
		}
		return nil
	})
	if err == nil {
		return dto, nil
	}
	return nil, err.(*fireback.IError)
}
