func CastParameterFromCli(c *cli.Context) *ParameterEntity {
	template := &ParameterEntity{}
	fireback.HandleXsrc(c, template)
	if c.IsSet("uid") {
		template.UniqueId = c.String("uid")
	}
	if c.IsSet("pid") {
		template.ParentId = fireback.NewStringAutoNull(c.String("pid"))
	}
	if c.IsSet("name") {
		template.Name = c.String("name")
	}
	if c.IsSet("description") {
		template.Description = c.String("description")
	}
	if c.IsSet("parameter-type-id") {
		template.ParameterTypeId = fireback.NewStringAutoNull(c.String("parameter-type-id"))
	}
	return template
}
