func ParameterTypeActionWipeClean(query fireback.QueryDSL) (int64, error) {
	var err error
	var count int64 = 0
	{
		subCount, subErr := fireback.WipeCleanEntity[ParameterTypeEntity]()
		if subErr != nil {
			fmt.Println("Error while wiping 'ParameterTypeEntity'", subErr)
			return count, subErr
		} else {
			count += subCount
		}
	}
	return count, err
}
func ParameterTypeActionBulkUpdate(
	query fireback.QueryDSL, dto *fireback.BulkRecordRequest[ParameterTypeEntity]) (
	*fireback.BulkRecordRequest[ParameterTypeEntity], *fireback.IError,
) {
	result := []*ParameterTypeEntity{}
	err := fireback.GetDbRef().Transaction(func(tx *gorm.DB) error {
		query.Tx = tx
		for _, record := range dto.Records {
			item, err := ParameterTypeActions.Update(query, record)
			if err != nil {
				return err
			} else {
				result = append(result, item)
			}
		}
		return nil
	})
	if err == nil {
		return dto, nil
	}
	return nil, err.(*fireback.IError)
}
